import { Configuration } from "./configuration";
import { ILooseObject } from "./looseObject";
import * as Amplitude from "@amplitude/node";

interface ITrackingManager {
  trackEvent(eventName: string, eventData: ILooseObject): void;
}
export class TrackingManager implements ITrackingManager {
  configuration?: Configuration;
  constructor(configuration: Configuration) {}

  trackEvent(eventName: string, eventData: ILooseObject): void {
    const amplitude = this.configuration?.getAmplitudeKey();
    const client = Amplitude.init(amplitude as string);
  }
}
